package ink.dkr;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// operatori inkrementiranja i dekrementiranja
		int i = 10;
		// primena operatora inkrementiranja
		// 3++ = 3+1 tj dva plusa povecavaju vrednost za jedan
		// ovo je unaran operator tj dovoljna je samo jedna promenljiva
		// i++
		// i += 1
		// i = i + 1
		
		i++;
		
		System.out.println(i);
		
		// operator dekrementiranja tj umanjenja vrednosti za 1
		// b = b - 1
		// b -= 1
		// b--
	
	    float b = 20.56f;
		b--;
		System.out.println(b);
		
		

	}

}
