package operatori;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
int a = 5;
int b = 50;
int zbir  = a + b;
System.out.println("Vrednost promenljive zbir je " + zbir);
// a i b su operandi a +,-,=,*,/ su operatori
int razlika = a - b;
System.out.println("Razlika je = " + razlika);

int proizvod = a*b;
System.out.println("Proizvod dve vrednosti je " + proizvod);

int kolicnik = b / a;
System.out.println("Kolicnik je = " + kolicnik);

float kolicnika = b/a;
System.out.println(kolicnika);
int ostatak = 10 % 3;
System.out.println("Ostatak je " + ostatak);

float tacnoDeljenje = 17f / 3f;
System.out.println("tacno deljenje je " + tacnoDeljenje);

double provera = (double)50/3;
System.out.println(provera);

	}

}
